import React, { useContext, useState } from "react";
import { Container, Row, Col, Button, ButtonGroup } from "react-bootstrap";
import { Web3SignIn } from "./components/account/Web3SignIn";
import { CurrentUserContext } from "./contexts/Store";
import { DepositForm } from "./components/DepositForm";
import { WithdrawForm } from "./components/WithdrawForm";
import { TokenInfo } from "./components/TokenInfo";
import styled from "styled-components";
import RaidLeft from "./assets/raid--left.png";
import RaidRight from "./assets/raid--right.png";

const AppDiv = styled.div`
  background-color: black;
  min-height: 100vh;
  min-width: 100vw;
  position: relative;
`;

const RaidLeftDiv = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  max-width: 25%;
  pointer-events: none;
  z-index: 0;
  img {
    max-width: 100%;
  }
`;

const RaidRightDiv = styled.div`
  position: absolute;
  top: 50%;
  right: 0;
  transform: translateY(-50%);
  max-width: 25%;
  pointer-events: none;
  z-index: 0;
  img {
    max-width: 100%;
  }
`;

const CenteredRow = styled(Row)`
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const CenteredCol = styled(Col)`
  align-items: center;
  justify-content: center;
`;

const HeaderRow = styled(Row)`
  align-items: center;
  justify-content: space-between;
`;

function App() {
  const [currentUser, setCurrentUser] = useContext(CurrentUserContext);
  const [wrapEth, setWrapEth] = useState(true);

  return (
    <AppDiv>
      <RaidLeftDiv>
        <img src={RaidLeft} />
      </RaidLeftDiv>
      <RaidRightDiv>
        <img src={RaidRight} />
      </RaidRightDiv>
      <Container>
        <HeaderRow>
          <h1>
            <span className="Outline">WRAP</span>ETH
          </h1>
          {currentUser && currentUser.username ? (
            <CenteredCol>
              <p>{currentUser.username}</p>
              <p>
                <TokenInfo />
              </p>
            </CenteredCol>
          ) : (
            <Web3SignIn setCurrentUser={setCurrentUser} />
          )}
        </HeaderRow>
        <ButtonGroup>
          <Button variant="primary" onClick={() => setWrapEth(true)}>
            Wrap ETH
          </Button>
          <Button variant="secondary" onClick={() => setWrapEth(false)}>
            Unwrap ETH
          </Button>
        </ButtonGroup>
        {wrapEth && (
          <>
            {currentUser && currentUser.username ? (
              <DepositForm />
            ) : (
              <p>Connect to Wrap Eth</p>
            )}
          </>
        )}
        {!wrapEth && (
          <>
            {currentUser && currentUser.username ? (
              <WithdrawForm />
            ) : (
              <p>Connect to Unwrap Eth</p>
            )}
          </>
        )}
      </Container>
    </AppDiv>
  );
}

export default App;
