import React, { useContext, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import { Web3SignIn } from "./components/account/Web3SignIn";
import { CurrentUserContext } from "./contexts/Store";
import { DepositForm } from "./components/DepositForm";
import { WithdrawForm } from "./components/WithdrawForm";
import { TokenInfo } from "./components/TokenInfo";
import styled from "styled-components";
import RaidLeft from "./assets/raid--left.png";
import RaidRight from "./assets/raid--right.png";

const AppDiv = styled.div`
  background-color: black;
  min-height: 100vh;
  min-width: 100vw;
  position: relative;
`;

const RaidLeftDiv = styled.div`
  position: absolute;
  top: 50%;
  left: 0;
  transform: translateY(-50%);
  max-width: 25%;
  img {
    max-width: 100%;
  }
`;

const RaidRightDiv = styled.div`
  position: absolute;
  top: 50%;
  right: 0;
  transform: translateY(-50%);
  max-width: 25%;
  img {
    max-width: 100%;
  }
`;

function App() {
  const [currentUser, setCurrentUser] = useContext(CurrentUserContext);
  const [wrapEth, setWrapEth] = useState(true);

  return (
    <AppDiv>
      <RaidLeftDiv>
        <img src={RaidLeft} />
      </RaidLeftDiv>
      <RaidRightDiv>
        <img src={RaidRight} />
      </RaidRightDiv>
      <Container>
        <Row>
          <Col>
            <h1>
              <span className="Outline">WRAP</span>ETH
            </h1>
          </Col>
          <Col>
            {currentUser && currentUser.username ? (
              <p>{currentUser.username}</p>
            ) : (
              <Web3SignIn setCurrentUser={setCurrentUser} />
            )}
          </Col>
        </Row>
        <Row>
          <button onClick={setWrapEth(true)}>Wrap ETH</button>
          <button onClick={setWrapEth(false)}>Unwrap ETH</button>
        </Row>
        <Row>
          <Col>
            {currentUser && currentUser.username && wrapEth ? (
              <DepositForm />
            ) : (
              <p>Must sign in first</p>
            )}
          </Col>
          <Col>
            <TokenInfo />
          </Col>
          <Col>
            {currentUser && currentUser.username && !wrapEth ? (
              <WithdrawForm />
            ) : (
              <p>Must sign in first</p>
            )}
          </Col>
        </Row>
      </Container>
    </AppDiv>
  );
}

export default App;
